import React from 'react';
import Card from 'react-bootstrap/Card'
import Button from 'react-bootstrap/esm/Button';
import AuthenticatedRoute from '../AuthenticatedRoute/AuthenticatedRoute';
import CreatePost from '../CreatePost/CreatePost';

const BlogCards = () => {

    let cardList: any = [];

    const blogTitles = ["blog1", "blog2", "blog3"]
    //temp data; will get from DB from API call

    const makeCard = (title: string, text: string, test: any) => {
        return(
            <Card style={{ width: '18rem' }}>
                <Card.Body>
                    <Card.Title>
                        {title}
                    </Card.Title>
                    <Button onClick={() => location.href = "gg.com"}>
                        {text}
                    </Button>
                </Card.Body>
            </Card>
        )
    }
    const pushCards = () => {
        for (let i = 0; i < blogTitles.length; i++) {
            const title = blogTitles[i];
            cardList.push(makeCard(title, "Blog", ""))
        } 
        cardList.push(makeCard("Create New Blog", "Create New Blog", <AuthenticatedRoute path="/create" component={CreatePost}/>))
    }

    pushCards();

    return (
        cardList
    );
};

export default BlogCards;
