import React from "react";
import Button from 'react-bootstrap/Button';
import { useAuth0 } from "@auth0/auth0-react";


const LoginButton = (props?: any) => {

  const { loginWithRedirect, isAuthenticated } = useAuth0();

  let textContent: string;
  (props.text) ? textContent = props.text : textContent = "Login"

  const createCookies = () => {
        document.cookie = `auth = ${isAuthenticated}`;
        document.cookie = `timestamp = ${Date.now()}`
        if (isAuthenticated) {
          document.cookie = `name= ${user.name}`;
        }
  }

return <Button variant="dark" onClick={() => {loginWithRedirect(); createCookies()}}>{textContent}</Button>;
};

export default LoginButton;
